package kz.shop.e_shop.db;

import java.util.ArrayList;

public class DBManager {

    private static ArrayList<ShopItem> items = new ArrayList<>();

    static {
        items.add(new ShopItem(1L, "IPhone 12 Pro MAX", "IPhone 12 Pro MAX, 512GB, 5G Network, Display 6.7, Frequency 6 GHZ", 2499, 100, 5, "https://encrypted-tbn0.gstatic.com/images?q=tbn%3AANd9GcQHkTH7o8fCGuAdQH_WKbUSGkh4tjMFMiKn7w&usqp=CAU"  ));
        items.add(new ShopItem(2L, "MEIZU 16 Pro", "MEIZU 16 Pro, Screen 6.2, 2232x1080 pixels, 8GB CPU RAM", 599, 100, 3, "data:image/jpeg;base64,"  ));
        items.add(new ShopItem(3L, "Samsung Galaxy 20", "Samsung Galaxy 20, Galaxy Fold Z, Z Flip 5G, Full HD, 256 GB Memary", 799, 100, 4, "https://encrypted-tbn0.gstatic.com/images?q=tbn%3AANd9GcQeTCmAAETd4RgPggQNJoKoE2RebROZg9KfJw&usqp=CAU"  ));
        items.add(new ShopItem(4L, "Nokia 9 Pureview", "Nokia 9 Pureview, SoC Qualcomm Snapdragon 845, GPU Adreno 630", 249, 100, 3, "https://encrypted-tbn0.gstatic.com/images?q=tbn%3AANd9GcS7fGBaPLvanXV80kimYxQL2n-lNu_x6Vqpxg&usqp=CAU"  ));

    }

    private static Long id = 5L;

    public static ArrayList<ShopItem> getItems(){
        return items;
    }

    public static void addItem(ShopItem item){
        item.setId(id);
        items.add(item);
        id++;
    }

    public static ShopItem getItem(Long id){
        for(ShopItem t : items){
            if(t.getId() == id) return t;
        }
        return null;
    }

    public static void deleteItem(Long id){
        for(int i=0;i<items.size();i++){
            if(items.get(i).getId().equals(id)){
                items.remove(i);
            }
        }
    }
}
